// Actual logic
function submitGuess() {
    if (!words.includes(currentGuess.toLowerCase())) return;
    guesses.push(currentGuess);
    currentGuess = '';
    updateGridColors();
    updateKeyboardColors();
    if (guesses.length === 6 && guesses[5] !== word) {
        document.getElementById('result-word').innerText = word;
        document.getElementById('result-dialog').showModal();
    }
}

function enterLetter(key) {
    if (key === 'ENTER') {
        if (currentGuess.length < 5) return;
        submitGuess();
        return;
    }
    // Put letter into grid
    if (key === 'BACKSPACE') {
        if (!currentGuess.length) return;
        const rowIndex = guesses.length + 1;
        const colIndex = currentGuess.length;
        document.getElementById(`gridCell-${rowIndex}-${colIndex}`).innerText = '';
        document.getElementById(`gridCell-${rowIndex}-${colIndex}`).style.border = '1px solid darkgray';
        currentGuess = currentGuess.slice(0, currentGuess.length - 1)
        return;
    }
    if (currentGuess.length >= 5) return;
    const rowIndex = guesses.length + 1;
    const colIndex = currentGuess.length + 1;
    document.getElementById(`gridCell-${rowIndex}-${colIndex}`).innerText = key;
    document.getElementById(`gridCell-${rowIndex}-${colIndex}`).style.border = '1px solid #787c7e';
    currentGuess += key;
}

function updateGridColors() {
    const rowIndex = guesses.length;
    const guess = guesses[guesses.length - 1];
    const greenCount = {};
    for (let i = 0; i < 5; i++) {
        const letter = guess[i];
        const cell = document.getElementById(`gridCell-${rowIndex}-${i + 1}`);
        cell.style.color = 'white';
        if (letter === word[i]) {
            if (greenCount[letter] === undefined) greenCount[letter] = 0;
            greenCount[letter] += 1;
            cell.style.backgroundColor = '#6aaa64';
            greenLetters.push(letter);
        }
    }
    const yellowCount = {};
    for (let i = 0; i < 5; i++) {
        const letter = guess[i];
        const cell = document.getElementById(`gridCell-${rowIndex}-${i + 1}`);
        if (letter === word[i]) continue;
        if (word.includes(letter)) {
            const count = word.split(letter).length - 1;
            // So we want to make the first "count" ones yellow
            if (yellowCount[letter] === undefined) yellowCount[letter] = 0;
            if (yellowCount[letter] >= (count - (greenCount[letter] || 0))) continue;
            yellowCount[letter] += 1;
            cell.style.backgroundColor = '#c9b458';
            yellowLetters.push(letter);
            continue;
        }
        cell.style.backgroundColor = '#787c7e';
        grayLetters.push(letter);
    }
}

function updateKeyboardColors() {
    // Update colors on keyboard
    for (const keyboardRow of keyboardRows) {
        for (const key of keyboardRow.letters) {
            const letter = document.getElementById(`letter-${key}`);
            if (grayLetters.includes(key)) letter.style.backgroundColor = '#787c7e';
            if (yellowLetters.includes(key)) letter.style.backgroundColor = '#c9b458';
            if (greenLetters.includes(key)) letter.style.backgroundColor = '#6aaa64';
            if (grayLetters.includes(key) || yellowLetters.includes(key) || greenLetters.includes(key)) letter.style.color = 'white';
        }
    }
}

// Create the grid UI
const gridContainer = document.getElementById('grid-container');
for (let i = 1; i <= 6; i++) {
    // Create an element for the row
    const gridRow = document.createElement('div');
    gridRow.classList.add('gridRow');
    gridContainer.appendChild(gridRow);
    // Create all the columns
    for (let j = 1; j <= 5; j++) {
        // Create an element for the row
        const gridCell = document.createElement('div');
        gridCell.classList.add('gridCell');
        gridCell.id = `gridCell-${i}-${j}`;
        gridRow.appendChild(gridCell);
    }
}

// Create the keyboard UI
// Create buttons for all letters
for (const keyboardRow of keyboardRows) {
    const rowContainer = document.getElementById(keyboardRow.id);
    for (const key of keyboardRow.letters) {
        const letter = document.createElement('button');
        letter.classList.add('letter');
        letter.id = `letter-${key}`
        letter.innerText = key;
        letter.onclick = () => enterLetter(key);
        rowContainer.appendChild(letter);
    }
}
// Create Enter and Backspace button
function addSpecialLetter(key) {
    const letter = document.createElement('button');
    letter.classList.add('letter');
    letter.classList.add(`letter-${key}`);
    letter.innerText = key.toUpperCase();
    letter.onclick = () => enterLetter(key.toUpperCase());
    key === 'enter'
        ? document.getElementById('row3').insertBefore(letter, document.getElementById('row3').firstChild)
        : document.getElementById('row3').appendChild(letter);
}
addSpecialLetter('enter');
addSpecialLetter('backspace');